export const _frontmatter = {
  "title": "When to use cast with AS and with explicit cast",
  "date": "2012-08-03",
  "categories": ["c-sharp"]
};

const layoutProps = {
  _frontmatter
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
