var jsonSerializerSettings = new JsonSerializerSettings { 
  PreserveReferencesHandling = PreserveReferencesHandling.Objects 
};

 class Program { static void Main(string[] args) { var p = new Program(); }

public Program() { var t1 = new Team() { TeamName = "MyTeam" }; var p1 = new Player { PlayerName = "Patrick" ,PlayerTeam = t1 }; var p2 = new Player() { PlayerName = "Melodie" , PlayerTeam = t1 }; t1.Players.Add(p1); t1.Players.Add(p2);

var settings = new JsonSerializerSettings() { PreserveReferencesHandling = PreserveReferencesHandling.Objects }; string json = JsonConvert.SerializeObject(p1, settings); Console.WriteLine(json); Console.ReadLine(); //Product deserializedProduct = JsonConvert.DeserializeObject<Player>(json);

} }

public class Player { public string PlayerName { get; set; } public Team PlayerTeam { get; set; } }

public class Team { public string TeamName { get; set; } public List<Player> Players { get; set; } public Team() { this.Players = new List<Player>(); } } 

