            "src": "/static/11cd2464fc3bf1aff3a5c6a346b909f3/004a5/sqltablenotification.png",
            "srcSet": ["/static/11cd2464fc3bf1aff3a5c6a346b909f3/5a46d/sqltablenotification.png 300w", "/static/11cd2464fc3bf1aff3a5c6a346b909f3/004a5/sqltablenotification.png 329w"],
