import { mdx } from '@mdx-js/react';
/* @jsxRuntime classic */
/* @jsx mdx */

export const _frontmatter = {
  "title": "Unable to launch the IIS Express Web server",
  "date": "2012-10-01",
  "categories": ["asp", "asp-mvc", "asp-net", "iis"]
};

const layoutProps = {
  _frontmatter
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
