      }}>{`public class Workout : BaseModel { public DateTime StartTime { get; set; } public DateTime? EndTime { get; set; } public string Name { get; set; } public string Goal { get; set; } public ICollection<WorkoutSession> Sessions { get; set; }

protected override IEnumerable<ValidationResult> ValidateModel(ValidationContext validationContext) { if (string.IsNullOrEmpty(Name)) { yield return new ValidationResult("Name is mandatory", new[] {"Name"}); } if (EndTime.HasValue) { if (StartTime > EndTime.Value) { yield return new ValidationResult("EndTime must be after the StartTime", new[] {"StartTime", "EndTime"}); } } } }

public class WorkoutSessionExercise : BaseModel { public int Order { get; set; } public string Repetitions { get; set; } public string Weights { get; set; } public string Tempo { get; set; } public TimeSpan RestBetweenSet { get; set; } public virtual Exercise Exercise { get; set; } public virtual WorkoutSession WorkoutSession { get; set; }

protected override IEnumerable<ValidationResult> ValidateModel(ValidationContext validationContext) { return new Collection<ValidationResult>(); } } 
`}</code></pre>
