            "src": "/static/21be9c74f372143194ef2400e18a7b76/c1b63/SimplifiedReactRedux.png",
            "srcSet": ["/static/21be9c74f372143194ef2400e18a7b76/5a46d/SimplifiedReactRedux.png 300w", "/static/21be9c74f372143194ef2400e18a7b76/0a47e/SimplifiedReactRedux.png 600w", "/static/21be9c74f372143194ef2400e18a7b76/c1b63/SimplifiedReactRedux.png 1200w", "/static/21be9c74f372143194ef2400e18a7b76/85053/SimplifiedReactRedux.png 1225w"],
