export const _frontmatter = {
  "title": "Unit Tests and Coverage Report in Jenkins using Jest from Create-React-App",
  "date": "2018-07-11",
  "categories": ["ci", "javascript", "jest", "nodejs-web", "react", "test"]
};

const layoutProps = {
  _frontmatter
