  }, " [TestMethod] public void GivenBatchSymbolsRenameInOrders_WhenListHasStockToBeRenamed_ThenOnlySpecificStocksAreRenamed() { // Arrange var uow = base.GetNewUnitOfWorkDapper(); var userId = this.runningContext.Object.GetUserId(); var repositoryExecuteContest = new ContestRepository(unitOfWorkForInsertion, uow, base.runningContext.Object); var repositoryExecuteModeration = new ModerationRepository(unitOfWorkForInsertion, base.GetNewUnitOfWorkDapper(), base.runningContext.Object); var repositoryUpdating = new OrderRepository(unitOfWorkForUpdate, base.GetNewUnitOfWorkDapper(), base.runningContext.Object); var repositoryReading = new OrderRepository(unitOfWorkForReading, base.GetNewUnitOfWorkDapper(), base.runningContext.Object); var date = this.runningContext.Object.GetCurrentTime(); var stock1 = new StockOrder { StockSymbol = new Symbol(\"msft\"), Quantity = 100, TransactionTypeId = TransactionType.Buy.Id, OrderStatusId = OrderStatusType.Waiting.Id, OrderTypeId = OrderType.Market.Id, PlacedOrderTime = date, ExpirationOrderTime = date, UserWhoPlacedTheOrderId = userId.ToString()}; var stock2 = new StockOrder { StockSymbol = new Symbol(\"msft2\"), Quantity = 100, TransactionTypeId = TransactionType.Buy.Id, OrderStatusId = OrderStatusType.Waiting.Id, OrderTypeId = OrderType.Market.Id, PlacedOrderTime = date, ExpirationOrderTime = date, UserWhoPlacedTheOrderId = userId.ToString() }; var stock3 = new StockOrder { StockSymbol = new Symbol(\"msft3\"), Quantity = 100, TransactionTypeId = TransactionType.Buy.Id, OrderStatusId = OrderStatusType.Waiting.Id, OrderTypeId = OrderType.Market.Id, PlacedOrderTime = date, ExpirationOrderTime = date, UserWhoPlacedTheOrderId = userId.ToString() }; var stock4 = new StockOrder { StockSymbol = new Symbol(\"msft\"), Quantity = 100, TransactionTypeId = TransactionType.Buy.Id, OrderStatusId = OrderStatusType.Waiting.Id, OrderTypeId = OrderType.Market.Id, PlacedOrderTime = date.AddDays(1), ExpirationOrderTime = date, UserWhoPlacedTheOrderId = userId.ToString() }; var symbolToRename = new SymbolToRename { CurrentSymbol = new Symbol(\"msft\") ,EffectiveDate = date ,SymbolAfterRename = new Symbol(\"goog\") ,UserWhoPlacedTheChangeId = this.runningContext.Object.GetUserId().ToString() ,Link = \"http://\" ,DateTimeChangePlaced = date };\n\nvar vote = SymbolToRenameVote.Create(symbolToRename, new ApplicationUser() { Id = this.runningContext.Object.GetUserId().ToString() }, 1, base.runningContext.Object); vote.Point = 100; vote.VoteDateTime = date;\n\n// Arrange - Create Contest var contest = contestModelBuilder.GetAStockContest(); repositoryExecuteContest.Save(contest); uow.Commit();\n\n// Arrange - Create Request to change Symbol symbolToRename = repositoryExecuteModeration.Save(symbolToRename); unitOfWorkForInsertion.Commit();\n\n// Arrange - Create Vote vote.SymbolToRename = symbolToRename; vote.SymbolToRename = null; vote.UserWhoVoted = null; unitOfWorkForInsertion.Entry(vote).State = EntityState.Added; unitOfWorkForInsertion.Commit();\n\n// Arrange - Create Portefolio var portefolio = repositoryExecuteContest.SaveUserToContest(contest, userId); unitOfWorkForInsertion.Commit(); var portefolioId = portefolio.Id;\n\n// Arrange - Create Stocks for Order stock1.PortefolioId = portefolioId; stock2.PortefolioId = portefolioId; stock3.PortefolioId = portefolioId; stock4.PortefolioId = portefolioId; unitOfWorkForInsertion.Entry(stock1).State = EntityState.Added; unitOfWorkForInsertion.Entry(stock2).State = EntityState.Added; unitOfWorkForInsertion.Entry(stock3).State = EntityState.Added; unitOfWorkForInsertion.Entry(stock4).State = EntityState.Added; unitOfWorkForInsertion.Commit();\n\n// Act repositoryUpdating.BatchSymbolsRenameInOrders(symbolToRename); unitOfWorkForUpdate.Commit();\n\n// Assert var stocks = repositoryReading.GetUserOrders(userId).ToList(); Assert.IsTrue(stocks.Any(d => d.StockSymbol.Value == \"goog\"), \"goog symbol should be present in the portefolio\"); Assert.AreEqual(1, stocks.Count(d => d.StockSymbol.Value == \"msft\"), \"msft symbol should be present once in the portefolio\"); } \n")), mdx("p", null, "A similar test with the new version:"), mdx("pre", null, mdx("code", {
